import { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';

const ModalForm = () => {
      const [formData, setFormData] = useState({
        username: '',
        phoneNumber: '',
        institutionName: '',
      });

      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        if (!formData.username || !formData.phoneNumber || !formData.institutionName) {
          alert('Please fill in all required fields.');
          return;
        }
        else if(!/^[6-9]\d{9}$/.test(formData.phoneNumber)){
          alert('Pleae enter valid mobile number');
          return;
        }
        else{
          handleClose()
          use('/user');
        }

      };
      const [show, setShow] = useState(false);

      const handleClose = () => setShow(false);
      const handleShow = () => setShow(true);

      useEffect(() => {
        handleShow();
      }, []);

  return (
    <>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton style={{backgroundColor:"#313D5A"}}>
          <Modal.Title style={{color:"whitesmoke"}}>User_Details</Modal.Title>
        </Modal.Header>
        <Modal.Body style={{backgroundColor:"#313D5A"}}>
          <Form onSubmit={handleSubmit} style={{backgroundColor:"#313D5A",color:"whitesmoke"}}>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>UserName</Form.Label>
              <Form.Control
                type="text"
                name="username"
                placeholder='username'
                value={formData.username}
                onChange={handleChange}
                required
                style={{backgroundColor:"#cfd4d1"}}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Contact Number</Form.Label>
              <Form.Control
                type="tel"
                name="phoneNumber"
                placeholder='phone/mobile'
                value={formData.phoneNumber}
                onChange={handleChange}
                required
                style={{backgroundColor:"#cfd4d1"}}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Institution Name</Form.Label>
              <Form.Control
                type="text"
                name="institutionName"
                placeholder='Enter Institution Name'
                value={formData.institutionName}
                onChange={handleChange}
                required
                style={{backgroundColor:"#cfd4d1"}}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer style={{backgroundColor:"#313D5A"}}>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ModalForm;
